  `inventory` int(10) NOT NULL DEFAULT '0' COMMENT '商品存貨量',
  `rank` tinyint(5) NOT NULL DEFAULT '0' COMMENT '商品評價',
  `sales` int(10) NOT NULL DEFAULT '0' COMMENT '銷售量',
  `pre_img` text COLLATE utf8_unicode_ci COMMENT '商品預覽圖片',
